pragma solidity ^0.6.0;

import "@openzeppelin/contracts-ethereum-package/contracts/math/Math.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC721/IERC721Enumerable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/Initializable.sol";

// Inheritance
import "./OwnableUpgradeSafe.sol";
import "./interfaces/IGallery.sol";
import "./interfaces/IGalleryErc721.sol";

contract FigureSwap is Initializable, OwnableUpgradeSafe {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    /* ========== CONSTANTS ========== */

    uint256 constant WINNING_PROPOSAL_COUNT = 10;
    uint256 constant WINNING_FIGURE_COUNT = 3;
    uint256 constant GOLD_CARD_POINT   = 100000 ether;
    uint256 constant SILVER_CARD_POINT = 50000 ether;
    uint256 constant COPPER_CARD_POINT = 30000 ether;
    uint256 constant NULL = 2**256 - 1;
    address constant DEAD_ADDRESS = 0x000000000000000000000000000000000000dEaD;

    /* ========== STATE VARIABLES ========== */

    address public galleryAddress;
    address public cardToken;
    address public pointToken;
    uint256[WINNING_FIGURE_COUNT] public cardPoints;
    // proposal id => figure id => tokenId of cardToken
    mapping(uint256 => mapping(uint256 => uint256)) public cardIds;
    // proposal id => figure id => if card has been swapped
    mapping(uint256 => mapping(uint256 => bool)) public cardsSwapped;

    /* ========== CONSTRUCTOR ========== */

    function initialize(
        address _galleryAddress,
        address _cardToken,
        address _pointToken
    )  external initializer {
        super.__Ownable_init();
        galleryAddress = _galleryAddress;
        cardToken = _cardToken;
        pointToken = _pointToken;
        cardPoints = [GOLD_CARD_POINT, SILVER_CARD_POINT, COPPER_CARD_POINT];
        for (uint256 proposalId = 0; proposalId < WINNING_PROPOSAL_COUNT; proposalId++) {
            for (uint256 figureId = 0; figureId < WINNING_FIGURE_COUNT; figureId++) {
                if (IGallery(galleryAddress).getTopFigure(proposalId, figureId) != NULL) {
                    uint256 tokenId = IERC721Enumerable(cardToken).totalSupply();
                    cardIds[proposalId][figureId] = tokenId;
                    IGalleryErc721(cardToken).mint(address(this), tokenId);
                }
            }
        }
    }

    /* ========== VIEWS ========== */

    /* ========== MUTATIVE FUNCTIONS ========== */

    function swap(uint256 proposalId, uint256 figureId) external {
        require(proposalId < WINNING_PROPOSAL_COUNT, "Invalid proposal id");
        require(figureId < WINNING_FIGURE_COUNT, "Invalid figure id");
        require(IGallery(galleryAddress).getTopFigure(proposalId, figureId) != NULL, "The id of figure is not available");
        require(!cardsSwapped[proposalId][figureId], "This card has been token");

        // set swap flag
        cardsSwapped[proposalId][figureId] = true;

        // send ERC20 token to contract
        uint256 amount = cardPoints[proposalId];
        IERC20(pointToken).safeTransferFrom(msg.sender, address(this), amount);

        // send ERC721 token to sender
        uint256 tokenId = cardIds[proposalId][figureId];
        IERC721(cardToken).safeTransferFrom(address(this), msg.sender, tokenId);

        emit Swapped(tokenId, amount);
    }

    function setGalleryAddress(address _galleryAddress) external onlyOwner {
        galleryAddress = _galleryAddress;
    }

    function setCardToken(address _cardToken) external onlyOwner {
        require(IERC721(_cardToken).supportsInterface(0x80ac58cd), "Invalid ERC721 contract");
        cardToken =_cardToken;
    }

    function setPointToken(address _pointToken) external onlyOwner {
        pointToken = _pointToken;
    }

    /* ========== EVENTS ========== */

    event Swapped(uint256 tokenId, uint256 amount);
}
