pragma solidity ^0.6.0;

import "@openzeppelin/contracts-ethereum-package/contracts/math/Math.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/Initializable.sol";

// Inheritance
import "./interfaces/IStakingRewards.sol";
import "./interfaces/IUniswapPriceOracle.sol";
import "./OwnableUpgradeSafe.sol";

contract StakingScore is Initializable, OwnableUpgradeSafe {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    /* ========== CONSTANTS ========== */

    uint256 constant STAKING_DAYS = 25;
    uint256 constant NULL = 2**256 - 1;
    address constant DEAD_ADDRESS = 0x000000000000000000000000000000000000dEaD;

    /* ========== STATE VARIABLES ========== */

    address public stakingToken;
    address public scoreToken;
    uint256 public startAt;
    uint256 public totalStake;
    mapping(address => uint256) public myStake;
    mapping(address => uint256) public myScore;
    // address => last time of claim reward
    mapping(address => uint) public lastClaimAt;

    /* ========== CONSTRUCTOR ========== */

    function initialize(
        address _stakingToken,
        address _scoreToken
    )  public initializer {
        super.__Ownable_init();
        stakingToken = _stakingToken;
        scoreToken = _scoreToken;
        startAt = block.timestamp;
    }

    /* ========== VIEWS ========== */

    function dayAt() public view returns (uint256) {
        return block.timestamp.sub(startAt).div(1 days);
    }

    /* ========== MUTATIVE FUNCTIONS ========== */

    function stake(uint256 amount) external {
        claimScore();

        require(dayAt() < STAKING_DAYS, "Expired");
        require(amount > 0, "Amount should be non-zero");

        // send ERC20 token to contract
        IERC20(stakingToken).safeTransferFrom(msg.sender, address(this), amount);

        // update stake value
        totalStake = totalStake.add(amount);
        myStake[msg.sender] = myStake[msg.sender].add(amount);

        emit Staked(amount);
    }

    function withdraw(uint256 amount) external {
        claimScore();

        require(amount > 0, "Amount should be non-zero");

        // update stake value
        myStake[msg.sender] = myStake[msg.sender].sub(amount);
        totalStake = totalStake.sub(amount);

        // send ERC20 token to sender
        IERC20(stakingToken).safeTransferFrom(address(this), msg.sender, amount);

        emit Withdrawn(amount);
    }

    function claimScore() public {
        // send ERC20 token to contract
        uint256 amount = calculateReward(msg.sender);
        if (amount > 0) {
            IERC20(scoreToken).safeTransferFrom(msg.sender, address(this), amount);
        }

        lastClaimAt[msg.sender] = block.timestamp;

        emit Claimed(amount);
    }

    function calculateReward(address target) public view returns (uint256) {
        uint256 span = block.timestamp.sub(lastClaimAt[target]);
        if (totalStake == 0) {
            return 0;
        }
        uint256 totalScore = IERC20(scoreToken).balanceOf(address(this));
        return totalScore.mul(myStake[target]).div(totalStake).mul(span).div(STAKING_DAYS);
    }

    /* ========== EVENTS ========== */

    event Staked(uint256 amount);
    event Withdrawn(uint256 amount);
    event Claimed(uint256 amount);
}
